import React, { useState } from 'react';

const EmpPostAJobPage = () => {
  const [formData, setFormData] = useState({
    jobtitle: '',
    jobcategory: '',
    jobtype: '',
    offeredsalary: '',
    experience: '',
    qualification: '',
    gender: '',
    country: '',
    city: '',
    location: '',
    latitude: '',
    longitude: '',
    email: '',
    website: '',
    estsince: '',
    completeaddress: '',
    description: '',
    startDate: '',
    endDate: ''
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch('/post-job', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData)
      });

      const result = await response.json();

      if (result.success) {
        alert('Job posted successfully');
      } else {
        alert('Error posting job');
      }
    } catch (error) {
      console.error('Error posting job:', error);
      alert('Error posting job');
    }
  };

  return (
    <div>
      <div className="wt-admin-right-page-header clearfix">
        <h2>Post a Job</h2>
        <div className="breadcrumbs"><a href="#">Home</a><a href="#">Dashboard</a><span>Job Submission Form</span></div>
      </div>

      <div className="panel panel-default">
        <div className="panel-heading wt-panel-heading p-a20">
          <h4 className="panel-title m-a0"><i className="fa fa-suitcase" />Job Details</h4>
        </div>
        <div className="panel-body wt-panel-body p-a20 m-b30 ">
          <form onSubmit={handleSubmit}>
            <div className="row">
              <div className="col-xl-4 col-lg-6 col-md-12">
                <div className="form-group">
                  <label>Job Title</label>
                  <input
                    className="form-control"
                    name="jobtitle"
                    type="text"
                    placeholder="e.g. Software Engineer"
                    value={formData.jobtitle}
                    onChange={handleChange}
                  />
                </div>
              </div>

              <div className="col-xl-4 col-lg-6 col-md-12">
                <div className="form-group">
                  <label>Job Category</label>
                  <select
                    className="wt-select-box selectpicker"
                    name="jobcategory"
                    value={formData.jobcategory}
                    onChange={handleChange}
                  >
                    <option value="">Select Category</option>
                    <option>Accounting and Finance</option>
                    <option>Clerical &amp; Data Entry</option>
                    <option>Human Resources</option>
                    <option>IT and Computers</option>
                    <option>Management</option>
                    {/* Add other options */}
                  </select>
                </div>
              </div>

              {/* Job Type */}
              <div className="col-xl-4 col-lg-6 col-md-12">
                <div className="form-group">
                  <label>Job Type</label>
                  <select
                    className="wt-select-box selectpicker"
                    name="jobtype"
                    value={formData.jobtype}
                    onChange={handleChange}
                  >
                    <option value="">Select Job Type</option>
                    <option>Full Time</option>
                    <option>Freelance</option>
                    <option>Part Time</option>
                    <option>Internship</option>
                  </select>
                </div>
              </div>

              {/* Offered Salary */}
              <div className="col-xl-4 col-lg-6 col-md-12">
                <div className="form-group">
                  <label>Offered Salary</label>
                  <select
                    className="wt-select-box selectpicker"
                    name="offeredsalary"
                    value={formData.offeredsalary}
                    onChange={handleChange}
                  >
                    <option value="">Select Salary</option>
                    <option>$500</option>
                    <option>$1000</option>
                    <option>$1500</option>
                    <option>$2000</option>
                  </select>
                </div>
              </div>

              {/* Experience */}
              <div className="col-xl-4 col-lg-6 col-md-12">
                <div className="form-group">
                  <label>Experience</label>
                  <input
                    className="form-control"
                    name="experience"
                    type="text"
                    placeholder="e.g. Minimum 3 years"
                    value={formData.experience}
                    onChange={handleChange}
                  />
                </div>
              </div>

              {/* Other fields like Qualification, Gender, Country, etc. */}
              {/* Add other form fields following the same pattern */}

              {/* Description */}
              <div className="col-md-12">
                <div className="form-group">
                  <label>Description</label>
                  <textarea
                    className="form-control"
                    name="description"
                    rows={3}
                    value={formData.description}
                    onChange={handleChange}
                    placeholder="Job description"
                  />
                </div>
              </div>

              <div className="col-md-12">
                <button type="submit" className="btn btn-primary">Post Job</button>
              </div>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default EmpPostAJobPage;
