import React, { useState,useEffect } from 'react';

import { useParams } from 'react-router-dom';
function SectionShareProfile() {


          const { jobId } = useParams(); // Destructure jobId from the URL params
          const [job, setJob] = useState(null); // State for storing job details
          const [loading, setLoading] = useState(true); // State to track loading
          const [error, setError] = useState(null); // State to track error

          useEffect(() => {
              // Fetch job details using jobId from the URL
              fetch(`http://localhost:8080/post-job/${jobId}`)
                  .then((response) => {
                      if (!response.ok) {
                          throw new Error('Error fetching job data');
                      }
                      return response.json(); // Parse JSON response
                  })
                  .then((data) => {
                      setJob(data); // Set job data to state
                      setLoading(false); // Set loading to false after data is fetched
                  })
                  .catch((err) => {
                      setError(err.message); // Handle errors
                      setLoading(false); // Set loading to false
                  });
          }, [jobId]); // Dependency on jobId, to refetch when it changes

          // Loading state
          if (loading) return <div>Loading...</div>;

          // Error state
          if (error) return <div>{error}</div>;

    return (
        <>
            <h4 className="twm-s-title">Share Profile</h4>
            <div className="twm-social-tags">
                <a href={job.userId.socialLinks.Facebook} className="fb-clr">Facebook</a>
                <a href={job.userId.socialLinks.Twitter} className="tw-clr">Twitter</a>
                <a href={job.userId.socialLinks.Linkedin} className="link-clr">Linkedin</a>
                <a href={job.userId.socialLinks.Whatsapp} className="whats-clr">Whatsapp</a>
                <a href={job.userId.socialLinks.Pinterest} className="pinte-clr">Pinterest</a>
            </div>
        </>
    )
}

export default SectionShareProfile;
