import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import {
  FacebookShareButton,
  FacebookIcon,
  TwitterShareButton,
  TwitterIcon,
  LinkedinShareButton,
  LinkedinIcon,
  WhatsappShareButton,
  WhatsappIcon,
  PinterestShareButton,
  PinterestIcon,
} from 'react-share';

function SectionShareProfile1() {
  const { jobId } = useParams();  // Extract jobId from the URL
  const [job, setJob] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    // Fetch job data using the jobId from the URL
    fetch(`http://localhost:8080/post-job/${jobId}`)
      .then((response) => {
        if (!response.ok) {
          throw new Error('Error fetching job data');
        }
        return response.json();
      })
      .then((data) => {
        setJob(data);
        setLoading(false);
      })
      .catch((err) => {
        setError(err.message);
        setLoading(false);
      });
  }, [jobId]);

  if (loading) return <div>Loading...</div>;
  if (error) return <div>{error}</div>;

  const shareUrl = window.location.href;  // Get the current page URL
  const title = job.title;  // Assuming job data has title
  const imageUrl = `${window.location.origin}/path-to-image.jpg`;  // Replace with actual image URL if available

  return (
    <>
      <h4 className="twm-s-title">Share Profile</h4>
      <div className="twm-social-tags">
        <FacebookShareButton url={shareUrl} quote={title} className="fb-clr">
          <FacebookIcon size={32} round />
          <span>Facebook</span>
        </FacebookShareButton>
        <TwitterShareButton url={shareUrl} title={title} className="tw-clr">
          <TwitterIcon size={32} round />
          <span>Twitter</span>
        </TwitterShareButton>
        <LinkedinShareButton url={shareUrl} title={title} className="link-clr">
          <LinkedinIcon size={32} round />
          <span>LinkedIn</span>
        </LinkedinShareButton>
        <WhatsappShareButton url={shareUrl} title={title} className="whats-clr">
          <WhatsappIcon size={32} round />
          <span>WhatsApp</span>
        </WhatsappShareButton>
        <PinterestShareButton media={imageUrl} url={shareUrl} className="pinte-clr">
          <PinterestIcon size={32} round />
          <span>Pinterest</span>
        </PinterestShareButton>
      </div>
    </>
  );
}

export default SectionShareProfile1;
