import { NavLink } from "react-router-dom";
import { publicUser } from "../../../../../globals/route-names";
import JobZImage from "../../../../common/jobz-img";
import SectionJobsSidebar1 from "../../sections/jobs/sidebar/section-jobs-sidebar1";
import SectionRecordsFilter from "../../sections/common/section-records-filter";
import SectionPagination from "../../sections/common/section-pagination";
import { useEffect, useState } from "react";
import { loadScript } from "../../../../../globals/constants";
import axios from "axios"; // Make sure axios is installed

function CandidateGridPage() {
    const [candidates, setCandidates] = useState([]);

    const _filterConfig = {
        prefix: "Showing",
        type: "Candidates",
        total: "2,150",
        showRange: false,
        showingUpto: ""
    }

    useEffect(() => {
        // Load external scripts
        loadScript("js/custom.js");

        // Fetch candidates data from the backend
        axios.get('http://localhost:8080/candidate')
            .then(response => {
                setCandidates(response.data); // Update the state with the fetched data
            })
            .catch(error => {
                console.error('There was an error fetching the candidates!', error);
            });
    }, []);

    return (
        <div className="section-full p-t120 p-b90 site-bg-white">
            <div className="container">
                <div className="row">
                    <div className="col-lg-4 col-md-12 rightSidebar">
                        <SectionJobsSidebar1 />
                    </div>
                    <div className="col-lg-8 col-md-12">
                        {/* Filter Short By */}
                        <SectionRecordsFilter _config={_filterConfig} />

                        <div className="twm-candidates-grid-wrap">
                            <div className="row">
                                {candidates.length > 0 ? (
                                    candidates.map(candidate => (
                                        <div key={candidate._id} className="col-lg-6 col-md-6">
                                            <div className="twm-candidates-grid-style1 mb-5">
                                                <div className="twm-media">
                                                    <div className="twm-media-pic">
                                                        <JobZImage src={  "default-image.jpg"} alt={candidate.name} />
                                                    </div>
                                                    <div className="twm-candidates-tag"><span>Featured</span></div>
                                                </div>
                                                <div className="twm-mid-content">
                                                    <NavLink to={publicUser.candidate.DETAIL1} className="twm-job-title">
                                                        <h4>{candidate.name}</h4>
                                                    </NavLink>
                                                    <p>{candidate.jobTitle}</p>
                                                    <NavLink to={publicUser.candidate.DETAIL1} className="twm-view-prifile site-text-primary">View Profile</NavLink>
                                                    <div className="twm-fot-content">
                                                        <div className="twm-left-info">
                                                            <p className="twm-candidate-address"><i className="feather-map-pin" />{candidate.location}</p>
                                                            <div className="twm-jobs-vacancies">{candidate.salary}<span>/ {candidate.salaryType}</span></div>
                                                        </div>
                                                    </div>
                                                </div>
                                            </div>
                                        </div>
                                    ))
                                ) : (
                                    <p>No candidates found</p>
                                )}
                            </div>

                            {/* Pagination Component */}
                            <SectionPagination />
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default CandidateGridPage;
