import { NavLink } from "react-router-dom";
import { publicUser } from "../../../../globals/route-names";
import JobZImage from "../../../common/jobz-img";
import SectionRecordsFilter from "../../public-user/sections/common/section-records-filter";
import { useEffect, useState } from "react";
import { loadScript } from "../../../../globals/constants";
import { useNavigate } from "react-router-dom";

function CanAppliedJobsPage() {
  const [applications, setApplications] = useState([]);
  const [loading, setLoading] = useState(true);

  const _filterConfig = {
    prefix: "Applied",
    type: "jobs",
    total: "250",
    showRange: false,
    showingUpto: ""
  };

  const navigate = useNavigate();

  useEffect(() => {
    loadScript("js/custom.js");
  }, []);

  useEffect(() => {
    // Check for candidateId in cookies
    const cookies = document.cookie.split("; ").reduce((acc, cookie) => {
      const [key, value] = cookie.split("=");
      acc[key] = value;
      return acc;
    }, {});

    if (!cookies.candidateId) {
      // Redirect to login page if candidateId cookie is not present
      navigate("/login");
    } else {
      // Fetch applications for the candidate
      fetch("http://localhost:8080/apply-job", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ candidateId: cookies.candidateId }),
      })
        .then((res) => res.json())
        .then((data) => {
          setApplications(data); // Set the data to state
          setLoading(false); // Stop loading
        })
        .catch((error) => {
          console.error('Error fetching applications:', error);
          setLoading(false);
        });
    }
  }, [navigate]);

  console.log(applications)
  return (
    <>
      <div className="twm-right-section-panel candidate-save-job site-bg-gray">
        {/* Filter Short By */}
        <SectionRecordsFilter _config={_filterConfig} />

        {loading ? (
          <p>Loading...</p> // Display loading text while fetching data
        ) : (
          <div className="twm-jobs-list-wrap">
            <ul>
              {applications.map((app) => (
                <li key={app._id}>
                  <div className="twm-jobs-list-style1 mb-5">
                    <div className="twm-media">
                      <JobZImage src={app.JobId.companylogo|| "images/default-job.jpg"} alt={app.JobId.jobtitle} />
                    </div>
                    <div className="twm-mid-content">
                      <NavLink to={publicUser.jobs.DETAIL1} className="twm-job-title">
                        <h4>{app.JobId.jobtitle} <span className="twm-job-post-duration">/ {new Date(app.JobId.startDate).toLocaleDateString()}</span></h4>
                      </NavLink>
                      <p className="twm-job-address">{app.JobId.location}</p>
                      <a href={app.JobId.companyWebsite} className="twm-job-websites site-text-primary">{app.JobId.companyWebsite}</a>
                    </div>
                    <div className="twm-right-content">
                      <div className="twm-jobs-category green">
                        <span className="twm-bg-green">New</span>
                      </div>
                      <div className="twm-jobs-amount">{app.JobId.offeredsalary} <span>/ Month</span></div>
                      <NavLink to={publicUser.jobs.DETAIL1} className="twm-jobs-browse site-text-primary">Apply Job</NavLink>
                    </div>
                  </div>
                </li>
              ))}
            </ul>
          </div>
        )}
      </div>
    </>
  );
}

export default CanAppliedJobsPage;
