import React, { useState, useEffect } from 'react';
import axios from 'axios';

function SectionCandidateSocialInfo() {
    // Initialize state for the form fields
    const [socialLinks, setSocialLinks] = useState({
        Facebook: '',
        Twitter: '',
        Linkdin: '',
        Whatsapp: '',
        Instagram: '',
        Pinterest: '',
        Tumbir: '',
        Youtube: '',
    });

    // Function to handle input changes
    const handleChange = (e) => {
        const { name, value } = e.target;
        setSocialLinks((prevState) => ({
            ...prevState,
            [name]: value,
        }));
    };

    // Handle form submission
    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            // Send the updated data to the server
            const response = await axios.post('/update-candidate', socialLinks, {
                withCredentials: true, // This ensures cookies are sent with the request
            });
            console.log('Candidate information updated:', response.data);
        } catch (error) {
            console.error('Error updating candidate information:', error);
        }
    };

    useEffect(() => {
        // Fetch existing social links when the component mounts (optional)
        const fetchSocialLinks = async () => {
            try {
                const response = await axios.get('/get-candidate-info', { withCredentials: true });
                setSocialLinks(response.data);
            } catch (error) {
                console.error('Error fetching candidate information:', error);
            }
        };
        fetchSocialLinks();
    }, []);

    return (
        <form onSubmit={handleSubmit}>
            <div className="panel panel-default">
                <div className="panel-heading wt-panel-heading p-a20">
                    <h4 className="panel-tittle m-a0">Social Network</h4>
                </div>
                <div className="panel-body wt-panel-body p-a20 m-b30 ">
                    <div className="row">
                        {Object.keys(socialLinks).map((key) => (
                            <div className="col-xl-4 col-lg-6 col-md-12" key={key}>
                                <div className="form-group">
                                    <label>{key}</label>
                                    <div className="ls-inputicon-box">
                                        <input
                                            className="form-control wt-form-control"
                                            name={key}
                                            type="text"
                                            placeholder={`https://www.${key.toLowerCase()}.com/`}
                                            value={socialLinks[key]}
                                            onChange={handleChange}
                                        />
                                        <i className={`fs-input-icon fab fa-${key.toLowerCase()}`} />
                                    </div>
                                </div>
                            </div>
                        ))}
                        <div className="col-lg-12 col-md-12">
                            <div className="text-left">
                                <button type="submit" className="site-button">
                                    Save Changes
                                </button>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </form>
    );
}

export default SectionCandidateSocialInfo;
