import React, { useState, useEffect } from "react";
import { useLocation, useParams } from "react-router-dom";
import { DropzoneComponent } from "react-dropzone-component";
import Cookies from "js-cookie";

function SectionApplyJob() {
  const [jobs, setJobs] = useState({});
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    message: "",
    answers: [],
    videoAnswers: [],
    resume: null,
    jobId: "",
  });

  const userId = Cookies.get("candidateId");
  const location = useLocation();
  const { jobId } = useParams();

  // Fetch job details
  useEffect(() => {
    const params = new URLSearchParams(location.search);
    const jobIdFromUrl = params.get("JobId");
    if (jobIdFromUrl) {
      setFormData((prev) => ({ ...prev, jobId: jobIdFromUrl }));
    }
  }, [location]);

  useEffect(() => {
    const fetchJobData = async () => {
      try {
        const response = await fetch(`http://localhost:8080/post-job/${jobId}`);
        if (response.ok) {
          const data = await response.json();
          setJobs(data);
        } else {
          console.error("Failed to fetch job data");
        }
      } catch (error) {
        console.error("Error fetching job data", error);
      }
    };

    if (jobId) {
      fetchJobData();
    }
  }, [jobId]);

  // Handle input changes for form fields
  const handleChange = (e) => {
    const { name, value } = e.target;

    if (name.startsWith("answer-")) {
      const index = parseInt(name.split("-")[1], 10);
      setFormData((prev) => {
        const newAnswers = [...prev.answers];
        newAnswers[index] = value;
        return { ...prev, answers: newAnswers };
      });
    } else if (name.startsWith("video-answer-")) {
      const index = parseInt(name.split("-")[2], 10);
      const videoFile = e.target.files[0];
      setFormData((prev) => {
        const newVideoAnswers = [...prev.videoAnswers];
        newVideoAnswers[index] = videoFile;
        return { ...prev, videoAnswers: newVideoAnswers };
      });
    } else {
      setFormData((prev) => ({
        ...prev,
        [name]: value,
      }));
    }
  };

  // Handle file changes for resume upload
  const handleFileChange = (file) => {
    setFormData((prev) => ({
      ...prev,
      resume: file,
    }));
  };

  // Submit the form
  const handleSubmit = async (e) => {
    e.preventDefault();

    const formDataToSend = new FormData();
    formDataToSend.append("name", formData.name);
    formDataToSend.append("email", formData.email);
    formDataToSend.append("message", formData.message);
    formDataToSend.append("JobId", jobId);
    formDataToSend.append("userId", userId);

    const questionsAndAnswers = jobs?.question?.map((q, index) => ({
      question: q,
      answer: formData.answers[index] || "",
      video: formData.videoAnswers[index] || null,
    }));

    formDataToSend.append("questionsAndAnswers", JSON.stringify(questionsAndAnswers));

    if (formData.resume) {
      formDataToSend.append("resume", formData.resume);
    }

    try {
      const response = await fetch("http://localhost:8080/apply-job", {
        method: "POST",
        body: formDataToSend,
        credentials: "include",
      });

      const data = await response.json();
      if (response.ok) {
        alert(data.message);
        console.log(data.message);
      } else {
        alert(data.error || "There was an error submitting your application.");
        console.log(data.error || "There was an error submitting your application.");
      }
    } catch (error) {
      console.error(error);
      alert("There was an error submitting your application.");
    }
  };

  return (
    <div className="max-w-4xl mx-auto p-6 bg-white shadow-lg rounded-lg">
      <form onSubmit={handleSubmit}>
        <h2 className="text-2xl font-semibold mb-6">Apply for Job</h2>

        {/* Name Field */}
        <div className="mb-4">
          <label className="block text-sm font-medium text-gray-700">Your Name</label>
          <input
            type="text"
            name="name"
            value={formData.name}
            onChange={handleChange}
            className="mt-1 block w-full px-4 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-indigo-500"
            placeholder="Devid Smith"
            required
          />
        </div>

        {/* Email Field */}
        <div className="mb-4">
          <label className="block text-sm font-medium text-gray-700">Email Address</label>
          <input
            type="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            className="mt-1 block w-full px-4 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-indigo-500"
            placeholder="Devid@example.com"
            required
          />
        </div>

        {/* Dynamic Questions and Answers */}
        {jobs?.question && jobs.question.length > 0 && (
          <div className="mb-6">
            {jobs.question.map((q, index) => (
              <div key={index} className="mb-4">
                <label className="block text-sm font-medium text-gray-700">{q}</label>
                <textarea
                  className="mt-1 block w-full px-4 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-indigo-500"
                  name={`answer-${index}`}
                  rows={3}
                  value={formData.answers[index] || ""}
                  onChange={handleChange}
                  placeholder="Your answer"
                />
                {/* Video Upload for Answer */}
                <div className="mt-2">
                  <label className="block text-sm font-medium text-gray-700">Upload Video Answer (Optional)</label>
                  <input
                    type="file"
                    name={`video-answer-${index}`}
                    accept="video/*"
                    onChange={handleChange}
                    className="mt-1 block w-full text-sm text-gray-500"
                  />
                </div>
              </div>
            ))}
          </div>
        )}

        {/* Message Field */}
        <div className="mb-4">
          <label className="block text-sm font-medium text-gray-700">Message</label>
          <textarea
            className="mt-1 block w-full px-4 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-indigo-500"
            name="message"
            value={formData.message}
            onChange={handleChange}
            rows={4}
            placeholder="Message sent to the employer"
          />
        </div>

        {/* Resume Upload */}
        <div className="mb-4">
          <label className="block text-sm font-medium text-gray-700">Upload Resume</label>
          <DropzoneComponent
            config={{ postUrl: "/apply-job" }}
            eventHandlers={{ addedfile: handleFileChange }}
            className="border-2 border-gray-300 p-4 rounded-md"
          />
          <small className="text-gray-500 mt-2 block">
            If you do not have a resume document, you may write your brief professional profile{" "}
            <a href="#" className="text-indigo-600">here</a>
          </small>
        </div>

        {/* Submit Button */}
        <div className="mb-4 text-center">
          <button type="submit" className="px-6 py-3 bg-indigo-600 text-white font-semibold rounded-lg shadow-md hover:bg-indigo-700 focus:outline-none">
            Send Application
          </button>
        </div>
      </form>
    </div>
  );
}

export default SectionApplyJob;
