import { useState, useEffect } from "react";
import { NavLink } from "react-router-dom";
import { publicUser } from "../../../../../../globals/route-names";
import SectionSideAdvert from "./section-side-advert";

function SectionJobsSidebar1() {
    const [category, setCategory] = useState("All Category");
    const [keyword, setKeyword] = useState("");
    const [location, setLocation] = useState("");
    const [jobType, setJobType] = useState([]);
    const [datePost, setDatePost] = useState(null);
    const [filteredJobs, setFilteredJobs] = useState([]); // Holds filtered jobs data

    // Sample job data (to be replaced with actual data from API or props)
    const jobsData = [
        { id: 1, title: "Web Designer", category: "Web Designer", jobType: "Freelance", location: "New York", datePosted: "2024-12-10" },
        { id: 2, title: "Full Stack Developer", category: "Developer", jobType: "Full Time", location: "San Francisco", datePosted: "2024-12-12" },
        { id: 3, title: "Accountant", category: "Accountant", jobType: "Part Time", location: "Los Angeles", datePosted: "2024-11-20" },
        // Add more job data...
    ];

    // Update filtered jobs based on filters
    useEffect(() => {
        let filtered = jobsData;

        if (category !== "All Category") {
            filtered = filtered.filter(job => job.category === category);
        }
        if (keyword) {
            filtered = filtered.filter(job => job.title.toLowerCase().includes(keyword.toLowerCase()));
        }
        if (location) {
            filtered = filtered.filter(job => job.location.toLowerCase().includes(location.toLowerCase()));
        }
        if (jobType.length > 0) {
            filtered = filtered.filter(job => jobType.includes(job.jobType));
        }
        if (datePost) {
            filtered = filtered.filter(job => job.datePosted >= datePost);
        }

        setFilteredJobs(filtered);
    }, [category, keyword, location, jobType, datePost]);

    return (
        <>
            <div className="side-bar">
                <div className="sidebar-elements search-bx">
                    <form>
                        {/* Category Filter */}
                        <div className="form-group mb-4">
                            <h4 className="section-head-small mb-4">Category</h4>
                            <select 
                                className="wt-select-bar-large selectpicker" 
                                data-live-search="true" 
                                value={category}
                                onChange={(e) => setCategory(e.target.value)}
                            >
                                <option>All Category</option>
                                <option>Web Designer</option>
                                <option>Developer</option>
                                <option>Accountant</option>
                            </select>
                        </div>

                        {/* Keyword Filter */}
                        <div className="form-group mb-4">
                            <h4 className="section-head-small mb-4">Keyword</h4>
                            <div className="input-group">
                                <input 
                                    type="text" 
                                    className="form-control" 
                                    placeholder="Job title or Keyword" 
                                    value={keyword} 
                                    onChange={(e) => setKeyword(e.target.value)} 
                                />
                                <button className="btn" type="button"><i className="feather-search" /></button>
                            </div>
                        </div>

                        {/* Location Filter */}
                        <div className="form-group mb-4">
                            <h4 className="section-head-small mb-4">Location</h4>
                            <div className="input-group">
                                <input 
                                    type="text" 
                                    className="form-control" 
                                    placeholder="Search location" 
                                    value={location} 
                                    onChange={(e) => setLocation(e.target.value)} 
                                />
                                <button className="btn" type="button"><i className="feather-map-pin" /></button>
                            </div>
                        </div>

                        {/* Job Type Filter */}
                        <div className="twm-sidebar-ele-filter">
                            <h4 className="section-head-small mb-4">Job Type</h4>
                            <ul>
                                {["Freelance", "Full Time", "Internship", "Part Time", "Temporary", "Volunteer"].map((type, idx) => (
                                    <li key={idx}>
                                        <div className="form-check">
                                            <input 
                                                type="checkbox" 
                                                className="form-check-input" 
                                                id={`jobType${idx}`} 
                                                checked={jobType.includes(type)}
                                                onChange={(e) => {
                                                    if (e.target.checked) {
                                                        setJobType([...jobType, type]);
                                                    } else {
                                                        setJobType(jobType.filter(t => t !== type));
                                                    }
                                                }} 
                                            />
                                            <label className="form-check-label" htmlFor={`jobType${idx}`}>{type}</label>
                                        </div>
                                    </li>
                                ))}
                            </ul>
                        </div>

                        {/* Date Posted Filter */}
                        <div className="twm-sidebar-ele-filter">
                            <h4 className="section-head-small mb-4">Date Posts</h4>
                            <ul>
                                {["Last hour", "Last 24 hours", "Last 7 days", "Last 14 days", "Last 30 days", "All"].map((dateOption, idx) => (
                                    <li key={idx}>
                                        <div className="form-check">
                                            <input 
                                                type="radio" 
                                                className="form-check-input" 
                                                id={`datePost${idx}`} 
                                                name="datePost"
                                                onChange={() => setDatePost(dateOption === "All" ? null : dateOption)}
                                            />
                                            <label className="form-check-label" htmlFor={`datePost${idx}`}>{dateOption}</label>
                                        </div>
                                    </li>
                                ))}
                            </ul>
                        </div>
                    </form>
                </div>
                <div className="widget tw-sidebar-tags-wrap">
                    <h4 className="section-head-small mb-4">Tags</h4>
                    <div className="tagcloud">
                        <NavLink to={publicUser.jobs.LIST}>General</NavLink>
                        <NavLink to={publicUser.jobs.LIST}>Jobs</NavLink>
                        <NavLink to={publicUser.jobs.LIST}>Payment</NavLink>
                        <NavLink to={publicUser.jobs.LIST}>Application</NavLink>
                        <NavLink to={publicUser.jobs.LIST}>Work</NavLink>
                    </div>
                </div>
            </div>

            {/* Display filtered jobs */}
            <div className="filtered-jobs-list">
                {filteredJobs.length > 0 ? (
                    filteredJobs.map(job => (
                        <div key={job.id} className="job-card">
                            <h5>{job.title}</h5>
                            <p>{job.category} - {job.jobType}</p>
                            <p>{job.location}</p>
                            <p>Posted on: {job.datePosted}</p>
                        </div>
                    ))
                ) : (
                    <p>No jobs found based on your filters.</p>
                )}
            </div>

            <SectionSideAdvert />
        </>
    );
}

export default SectionJobsSidebar1;
